public void sendData(String database, String table, String content, boolean partitioned) throws Exception {
    String url = "http://localhost:8040/api/" + database + "/" + table + "/_stream_load";
    if (partitioned) {
        url += "?partitioned=true";
    }

    HttpClient client = HttpClient.newHttpClient();
    HttpRequest request = HttpRequest.newBuilder()
           .uri(URI.create(url))
           .PUT(HttpRequest.BodyPublishers.ofString(content))
           .build();

    HttpResponse<String> response = client.send(request, HttpResponse.BodyHandlers.ofString());

    if (response.statusCode()!= 200) {
        throw new Exception("Stream load failed with status code: " + response.statusCode());
