public Range<BigDecimal> calcInterquartileRangeApproximation(List<BigDecimal> sortedValues) {
    if (sortedValues == null || sortedValues.isEmpty()) {
        return Range.closedOpen(BigDecimal.ZERO, BigDecimal.ZERO);
    }

    BigDecimal q1 = calcPercentile(sortedValues, 25);
    BigDecimal q3 = calcPercentile(sortedValues, 75);

    if (q3.subtract(q1).compareTo(BigDecimal.ZERO) == 0) {
        if (sortedValues.get(sortedValues.size() - 1).compareTo(q3) == 0) {
            sortedValues.remove(sortedValues.size() - 1);
            return calcInterquartileRangeApproximation(sortedValues);
        }
        return Range.closedOpen(q1, q3);
    }

    return Range.closedOpen(q1, q3);
}

private BigDecimal calcPercentile(List<BigDecimal> sortedValues, int percentile) {
    int index = (int) Math.ceil(percentile / 100.0 * sortedValues.size());
    return sortedValues.get(index - 1);
}
