def compute_parameter_entropy(
    parameter_config: vz.ParameterConfig,
    parameter_values: Iterable[Optional[vz.ParameterValue]],
) -> float :
    """
    Description: This function computes the entropy of a parameter based on its configuration and values.

    Args:
        parameter_config (vz.ParameterConfig): The configuration of the parameter.
        parameter_values (Iterable[Optional[vz.ParameterValue]]): The values of the parameter.

    Returns:
        float: The entropy of the parameter.
    """
    if parameter_config.type == vz.ParameterType.CATEGORICAL:
        value_counts = np.bincount([v for v in parameter_values if v is not None])
        probabilities = value_counts / np.sum(value_counts)
        entropy = scipy.stats.entropy(probabilities)
    elif parameter_config.type == vz.ParameterType.DISCRETE:
        entropy = 0.0
        for value in parameter_values:
            if value is not None:
                entropy += -value * np.log2(value)
    elif parameter_config.type == vz.ParameterType.DOUBLE:
        entropy = 0.0
        for value in parameter_values:
            if value is not None:
                entropy += -value * np.log2(value)
    elif parameter_config.type == vz.ParameterType.INTEGER:
        entropy = 0.0
        for value in parameter_values:
            if value is not None:
                entropy += -value * np.log2(value)
    else:
        raise ValueError(f"Unsupported parameter type: {parameter_config.type}")

    return entropy
