                    ],
                    axis=1,
                )

    if not keep_partial_nan_rows:
        df = overwrite_partial_rows_with_nan(df)

    # nan last row
    df = df.iloc[:-1].reindex(df.index)
    return df

def sample(self):
  node = self.root
  while isinstance(node, Node):
    uprobs = np.array([x.uprob for x in node.children])
    total = uprobs.sum()
    if not np.isfinite(total):
      finite = np.isinf(uprobs)
      probs = finite / finite.sum()
    elif total == 0:
      probs = np.ones(len(uprobs)) / len(uprobs)
    else:
      probs = uprobs / total
    choice = self.rng.choice(np.arange(len(uprobs)), p=probs)
    node = node.children[choice.item()]
  return node.key

