def _setup_cuda_device(device: torch.device) -> torch.device:
    local_rank = _get_local_rank() or 0
    if device.index is None:
        device = torch.device(type="cuda", index=local_rank)

    # Ensure index is available before setting device
    if device.index >= torch.cuda.device_count():
        raise RuntimeError(
            "The local rank is larger than the number of available GPUs."
        )

    torch.cuda.set_device(device)
    return device

def to_numpy(
    evset: EventSet,
    timestamp_to_datetime: bool = True,
    timestamps: bool = True,
) -> Dict[str, ndarray]:
    timestamp_key = "timestamp"
    index_names = evset.schema.index_names()
    feature_names = evset.schema.feature_names()

    column_names = index_names + feature_names
    if timestamps:
        column_names += [timestamp_key]

    dst = {column_name: [] for column_name in column_names}
    for index, data in evset.data.items():
        assert isinstance(index, tuple)

        if timestamps:
            # Timestamps
            if evset.schema.is_unix_timestamp and timestamp_to_datetime:
                dst[timestamp_key].append(
                    data.timestamps.astype("datetime64[s]")
                )
            else:
                dst[timestamp_key].append(data.timestamps)

        # Features
        for feature_name, feature in zip(feature_names, data.features):
            dst[feature_name].append(feature)

        # Indexes
        num_timestamps = len(data.timestamps)
        for index_name, index_item in zip(index_names, index):
            dst[index_name].append(np.repeat(index_item, num_timestamps))

    dst = {k: np.concatenate(v) for k, v in dst.items()}
    return dst

def _get_user_settings(self, session: HubSession) -> HubUserSettings:
    access_token = session.access_token.get_secret_value()
    token_type = session.token_type
    authorization = f"{token_type.title()} {access_token}"

    response = get(
        url=self._base_url + "/terminal/user",
        headers={"Authorization": authorization},
        timeout=self.TIMEOUT,
    )
    if response.status_code == 200:
        user_settings = response.json()
        filtered = {k: v for k, v in user_settings.items() if v is not None}
        return HubUserSettings.model_validate(filtered)
    status_code = response.status_code
    detail = response.json().get("detail", None)
    raise HTTPException(status_code, detail)

