def find_dominant_color(
    image: np.ndarray, black_threshold: int = 50
) -> Tuple[Union[int, str], Union[int, str], Union[int, str]]:
  pixels = image.reshape(-1, 3)

  # Filter out black pixels based on the threshold
  non_black_pixels = pixels[(pixels > black_threshold).any(axis=1)]

  if non_black_pixels.size != 0:
    kmeans = sklearn_cluster.KMeans(
        n_clusters=1, n_init=10, random_state=0
    ).fit(non_black_pixels)
    dominant_color = kmeans.cluster_centers_[0].astype(int)

  else:
    dominant_color = ['Na', 'Na', 'Na']
  return tuple(dominant_color)

def body_from_data(
    *,
    data: oai.Operation,
    schemas: Schemas,
    config: Config,
    endpoint_name: str,
) -> Tuple[List[Union[Body, ParseError]], Schemas]:
    if data.request_body is None or isinstance(data.request_body, oai.Reference):
        return [], schemas

    bodies: List[Union[Body, ParseError]] = []
    body_content = data.request_body.content
    prefix_type_names = len(body_content) > 1

    for content_type, media_type in body_content.items():
        simplified_content_type = get_content_type(content_type, config)
        if simplified_content_type is None:
            bodies.append(
                ParseError(
                    detail="Invalid content type",
                    data=data.request_body,
                    level=ErrorLevel.WARNING,
                )
            )
            continue
        media_type_schema = media_type.media_type_schema
        if media_type_schema is None:
            bodies.append(
                ParseError(
                    detail="Missing schema",
                    data=data.request_body,
                    level=ErrorLevel.WARNING,
                )
            )
            continue
        if simplified_content_type == "application/x-www-form-urlencoded":
            body_type = BodyType.DATA
        elif simplified_content_type == "multipart/form-data":
            body_type = BodyType.FILES
        elif simplified_content_type == "application/octet-stream":
            body_type = BodyType.CONTENT
        elif simplified_content_type == "application/json" or simplified_content_type.endswith("+json"):
            body_type = BodyType.JSON
        else:
            bodies.append(
                ParseError(
                    detail=f"Unsupported content type {simplified_content_type}",
                    data=data.request_body,
                    level=ErrorLevel.WARNING,
                )
            )
            continue
        prop, schemas = property_from_data(
            name="body",
            required=True,
            data=media_type_schema,
            schemas=schemas,
            parent_name=f"{endpoint_name}_{body_type}" if prefix_type_names else endpoint_name,
            config=config,
        )
        if isinstance(prop, ParseError):
            bodies.append(prop)
            continue
        if isinstance(prop, ModelProperty) and body_type == BodyType.FILES:
            # Regardless of if we just made this property or found it, it now needs the `to_multipart` method
            prop = attr.evolve(prop, is_multipart_body=True)
            schemas = attr.evolve(
                schemas,
                classes_by_name={
                    **schemas.classes_by_name,
                    prop.class_info.name: prop,
                },
            )
        bodies.append(
            Body(
                content_type=content_type,
                prop=prop,
                body_type=body_type,
            )
        )

    return bodies, schemas

def compute_temperature_features(
    meter_data_index,
    temperature_data,
    heating_balance_points=None,
    cooling_balance_points=None,
    data_quality=False,
    temperature_mean=True,
    degree_day_method="daily",
    percent_hourly_coverage_per_day=0.5,
    percent_hourly_coverage_per_billing_period=0.9,
    use_mean_daily_values=True,
    tolerance=None,
    keep_partial_nan_rows=False,
):
    if temperature_data.index.freq != "H":
        raise ValueError(
            "temperature_data.index must have hourly frequency (freq='H')."
            " Found: {}".format(temperature_data.index.freq)
        )

    if not temperature_data.index.tz:
        raise ValueError(
            "temperature_data.index must be timezone-aware. You can set it with"
            " temperature_data.tz_localize(...)."
        )

    if meter_data_index.freq is None and meter_data_index.inferred_freq == "H":
        raise ValueError(
            "If you have hourly data explicitly set the frequency"
            " of the dataframe by setting"
            "``meter_data_index.freq ="
            " pd.tseries.frequencies.to_offset('H')."
        )

    if not meter_data_index.tz:
        raise ValueError(
            "meter_data_index must be timezone-aware. You can set it with"
            " meter_data.tz_localize(...)."
        )

    if meter_data_index.duplicated().any():
        raise ValueError("Duplicates found in input meter trace index.")

    temp_agg_funcs = []
    temp_agg_column_renames = {}

    if heating_balance_points is None:
        heating_balance_points = []
    if cooling_balance_points is None:
        cooling_balance_points = []

    if meter_data_index.freq is not None:
        try:
            freq_timedelta = pd.Timedelta(meter_data_index.freq)
        except ValueError:  # freq cannot be converted to timedelta
            freq_timedelta = None
    else:
        freq_timedelta = None

    if tolerance is None:
        tolerance = freq_timedelta

    if not (heating_balance_points == [] and cooling_balance_points == []):
        if degree_day_method == "hourly":
            pass
        elif degree_day_method == "daily":
            if meter_data_index.freq == "H":
                raise ValueError(
                    "degree_day_method='daily' must be used with"
                    " daily meter data. Found: 'hourly'".format(degree_day_method)
                )
        else:
            raise ValueError("method not supported: {}".format(degree_day_method))

    if freq_timedelta == pd.Timedelta("1H"):
        # special fast route for hourly data.
        df = temperature_data.to_frame("temperature_mean").reindex(meter_data_index)

        if use_mean_daily_values:
            n_days = 1
        else:
            n_days = 1.0 / 24.0

        df = df.assign(
            **{
                "cdd_{}".format(bp): np.maximum(df.temperature_mean - bp, 0) * n_days
                for bp in cooling_balance_points
            }
        )
        df = df.assign(
            **{
                "hdd_{}".format(bp): np.maximum(bp - df.temperature_mean, 0) * n_days
                for bp in heating_balance_points
            }
        )
        df = df.assign(
            n_hours_dropped=df.temperature_mean.isnull().astype(int),
            n_hours_kept=df.temperature_mean.notnull().astype(int),
        )
        # TODO(philngo): bad interface or maybe this is just wrong for some reason?
        if data_quality:
            df = df.assign(
                temperature_null=df.n_hours_dropped,
                temperature_not_null=df.n_hours_kept,
            )
        if not temperature_mean:
            del df["temperature_mean"]
    else:
        # daily/billing route
        # heating/cooling degree day aggregations. Needed for n_days fields as well.
        temp_agg_funcs.extend(
            _degree_day_columns(
                heating_balance_points=heating_balance_points,
                cooling_balance_points=cooling_balance_points,
                degree_day_method=degree_day_method,
                percent_hourly_coverage_per_day=percent_hourly_coverage_per_day,
                percent_hourly_coverage_per_billing_period=percent_hourly_coverage_per_billing_period,
                use_mean_daily_values=use_mean_daily_values,
            )
        )
        temp_agg_column_renames.update(
            {("temp", "degree_day_columns"): "degree_day_columns"}
        )

        if data_quality:
            temp_agg_funcs.extend(
                [("not_null", "count"), ("null", lambda x: x.isnull().sum())]
            )
            temp_agg_column_renames.update(
                {
                    ("temp", "not_null"): "temperature_not_null",
                    ("temp", "null"): "temperature_null",
                }
            )

        if temperature_mean:
            temp_agg_funcs.extend([("mean", "mean")])
            temp_agg_column_renames.update({("temp", "mean"): "temperature_mean"})

        # aggregate temperatures
        temp_df = temperature_data.to_frame("temp")
        temp_groups = _matching_groups(meter_data_index, temp_df, tolerance)
        temp_aggregations = temp_groups.agg({"temp": temp_agg_funcs})

        # expand temp aggregations by faking and deleting the `meter_value` column.
        # I haven't yet figured out a way to avoid this and get the desired
        # structure and behavior. (philngo)
        meter_value = pd.DataFrame({"meter_value": 0}, index=meter_data_index)
        df = pd.concat([meter_value, temp_aggregations], axis=1).rename(
            columns=temp_agg_column_renames
        )
        del df["meter_value"]

        if "degree_day_columns" in df:
            if df["degree_day_columns"].dropna().empty:
                column_defaults = {
                    column: np.full(df["degree_day_columns"].shape, np.nan)
                    for column in ["n_days_dropped", "n_days_kept"]
                }
                df = df.drop(["degree_day_columns"], axis=1).assign(**column_defaults)
            else:
                df = pd.concat(
                    [
                        df.drop(["degree_day_columns"], axis=1),
                        df["degree_day_columns"].dropna().apply(pd.Series),
                    ],
                    axis=1,
                )

    if not keep_partial_nan_rows:
        df = overwrite_partial_rows_with_nan(df)

    # nan last row
    df = df.iloc[:-1].reindex(df.index)
    return df

def polyvalfromroots(x, r, tensor=True):
    r = np.array(r, ndmin=1, copy=False)
    if r.dtype.char in '?bBhHiIlLqQpP':
        r = r.astype(np.double)
    if isinstance(x, (tuple, list)):
        x = np.asarray(x)
    if isinstance(x, np.ndarray):
        if tensor:
            r = r.reshape(r.shape + (1,)*x.ndim)
        elif x.ndim >= r.ndim:
            raise ValueError("x.ndim must be < r.ndim when tensor == False")
    return np.prod(x - r, axis=0)

def sample(self):
  node = self.root
  while isinstance(node, Node):
    uprobs = np.array([x.uprob for x in node.children])
    total = uprobs.sum()
    if not np.isfinite(total):
      finite = np.isinf(uprobs)
      probs = finite / finite.sum()
    elif total == 0:
      probs = np.ones(len(uprobs)) / len(uprobs)
    else:
      probs = uprobs / total
    choice = self.rng.choice(np.arange(len(uprobs)), p=probs)
    node = node.children[choice.item()]
  return node.key

def allocate(self, num_blocks: int) -> torch.Tensor:
    if num_blocks > self._free_blocks:
        raise ValueError(f'Not enough free blocks in the KV-cache to allocate {num_blocks} blocks')

    allocated_blocks = torch.zeros(num_blocks, dtype=torch.int32)
    for i in range(num_blocks):
        allocated_blocks[i] = self._head
        self._head = self._blocks[self._head].item()
        self._blocks[allocated_blocks[i]] = -1  # Mark as used
        self._free_blocks -= 1

    return allocated_blocks

def get_dtype(
    dtype: Optional[str] = None, device: Optional[torch.device] = None
) -> torch.dtype:

    # None defaults to float32
    if dtype is None:
        return torch.float32

    # Convert to torch.dtype
    torch_dtype = PRECISION_STR_TO_DTYPE.get(dtype, dtype)

    # dtype must be one of the supported precisions
    if torch_dtype not in PRECISION_STR_TO_DTYPE.values():
        raise ValueError(
            f"Dtype {torch_dtype} must be one of {', '.join(list_dtypes())} for finetuning."
        )

    # TODO (rohan-varma): prefer to use get_default_device() here to figure out whether user is training on
    # CPU or GPU, but it is not supported in versions of torch we test.
    if (
        torch_dtype == torch.bfloat16
        and device != torch.device("cpu")
        and not verify_bf16_support()
    ):
        raise RuntimeError(
            "bf16 precision was requested but not available on this hardware. Please use fp32 precision instead."
        )

    return torch_dtype

def generate_openapi(self, routes: list[BaseRoute] | None = None):
    openapi_base = get_openapi(
        title=self.name,
        version=self.version,
        routes=(routes if routes is not None else self.app.routes),
    )

    #
    # Exception injection
    #

    # Loop over the registered controllers and get the action exceptions
    exceptions_by_url: dict[str, list[ExceptionSchema]] = {}
    for controller_definition in self.controllers:
        for (
            _,
            _,
            metadata,
        ) in controller_definition.controller._get_client_functions():
            url = controller_definition.get_url_for_metadata(metadata)
            # Not included in the specified routes, we should ignore this controller
            if url not in openapi_base["paths"]:
                continue

            exceptions_models = metadata.get_exception_models()
            if not exceptions_models:
                continue

            exceptions_by_url[url] = [
                self._format_exception_model(exception_model)
                for exception_model in exceptions_models
            ]

    # Users are allowed to reference the same schema name multiple times so long
    # as they have the same value. If they use conflicting values we'll have
    # to use the long name instead of the short module name to avoid conflicting
    # schema definitions.
    schema_names_to_long: defaultdict[str, set[str]] = defaultdict(set)
    for exception_payloads in exceptions_by_url.values():
        for payload in exception_payloads:
            schema_names_to_long[payload.schema_name].add(payload.schema_name_long)

    duplicate_schema_names = {
        schema_name
        for schema_name, schema_name_longs in schema_names_to_long.items()
        if len(schema_name_longs) > 1
    }

    for url, exception_payloads in exceptions_by_url.items():
        existing_status_codes: set[int] = set()

        for payload in exception_payloads:
            # Validate the exception state doesn't override existing values
            # Status codes are local to this particular endpoint but schema names
            # are global because they're placed in the global components section
            if payload.status_code in existing_status_codes:
                raise ValueError(
                    f"Duplicate status code {payload.status_code} for {url}"
                )

            schema_name = (
                payload.schema_name
                if payload.schema_name not in duplicate_schema_names
                else payload.schema_name_long
            )

            other_definitions = {
                definition_name: self._update_ref_path(definition)
                for definition_name, definition in payload.schema_value.pop(
                    "$defs", {}
                ).items()
            }
            openapi_base["components"]["schemas"].update(other_definitions)
            openapi_base["components"]["schemas"][
                schema_name
            ] = self._update_ref_path(payload.schema_value)

            # All actions are "posts" by definition
            openapi_base["paths"][url]["post"]["responses"][
                str(payload.status_code)
            ] = {
                "description": f"Custom Error: {payload.schema_name}",
                "content": {
                    "application/json": {
                        "schema": {"$ref": f"#/components/schemas/{schema_name}"}
                    }
                },
            }

            existing_status_codes.add(payload.status_code)

    return openapi_base
